import './App.css';
import { useState, useEffect } from 'react'

const Persons = (props) => {
  const {persons, filter} = props;
  const filteredPersons = persons.filter(p => p.name.includes(filter))

  return (
    <div>
      {filteredPersons.map(p => <div key={p.name}>
        {p.name} {p.number} </div>)}
    </div>
  );
}

const Filter = (props) => {
  const {onChange, value} = props;
  return <input value={value} onChange={onChange}></input>
}

const PersonForm = (props) => {
  const {onSubmit, newName, onChangeNewName, newNumber, onChangeNewNumber} = props

  return <form onSubmit={onSubmit}>
    <div>
      name: <input value={newName} onChange={onChangeNewName}/>
      <br/>
      number: <input value={newNumber} onChange={onChangeNewNumber}/>
    </div>
    <div>
      <button type="submit">add</button>
    </div>
  </form> 
}

const App = () => {
  // states
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' ,
      number: "408-000-1200"
    }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('');
  const [newFilter, setNewFilter] = useState('');

   // fetch from database
   const hook = () => {
    console.log('effect')
    axios
      .get('http://localhost:3001/persons')
      .then(response => {
        console.log('promise fulfilled')
        setPersons(response.data)
      })
  }

  useEffect(hook, [])


  // onChange functions
  const onChangeNewName = (event) => {
    setNewName(event.target.value);
  };
  const onChangeNewNumber = (event) => {
    setNewNumber(event.target.value);
  };
  const onSubmit = (event) => {
    event.preventDefault();
    if(persons.some(p => p.name === newName)){
      alert(`${newName} is already added to phonebook`)
    } else {
      setPersons(persons.concat({name : newName, number : newNumber}));
    }
    setNewNumber("");
    setNewName("");
  }

  const onFilterChange = (event) => {
    setNewFilter(event.target.value);
  }

  return (
    <div>
      <h1>Phonebook</h1>
      Search <Filter value={newFilter} onChange={onFilterChange}/>
      <br/>
      <br/>

      <h2>Add a new contact</h2>

      <PersonForm 
        newName={newName}
        onChangeNewName={onChangeNewName}
        newNumber={newNumber}
        onChangeNewNumber={onChangeNewNumber}
        onSubmit={onSubmit}
      />

      <h2>Numbers</h2>
      <Persons persons={persons} filter={newFilter}/>
    </div>
  )
}

export default App
