import './App.css';
import { useState, useEffect } from 'react'
import axios from 'axios'
import personService from './services.js'
import Persons from './Persons'
import Filter from './Filter'
// import PersonForm from './PersonForm'


const PersonForm = (props) => {
  const {onSubmit, newName, onChangeNewName, newNumber, onChangeNewNumber} = props

  return <form onSubmit={onSubmit}>
      <div>
      name: <input value={newName} onChange={onChangeNewName}/>
      <br/>
      number: <input value={newNumber} onChange={onChangeNewNumber}/>
      </div>
      <div>
      <button type="submit">add</button>
      </div>
  </form> 
}

const App = () => {
  // states
  const [persons, setPersons] = useState([]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('');
  const [newFilter, setNewFilter] = useState('');

   // fetch from database
   const fetch = () => {
    personService
      .getAll()
      .then(response => {
        console.log('promise fulfilled')
        setPersons(response)
      })
  }
  useEffect(fetch, [])


  // onChange functions
  const onChangeNewName = (event) => {
    setNewName(event.target.value);
  };

  const onChangeNewNumber = (event) => {
    setNewNumber(event.target.value);
  };

  const onDelete = (id) => {
    return () => {
      if(!window.confirm("Are you sure you want to delete this contact? "))
        return;

      personService.removePerson(id).then(
        () => setPersons(persons.filter(p => p.id !== id))
      );
    }

  }

  const onAddContact = (event) => {
    event.preventDefault();
    if(persons.some(p => p.name === newName)){
      alert(`${newName} is already added to phonebook`);
      return;
    } 
    // posting data to database
    const newContact = ({name : newName, number : newNumber});
    personService
      .addPerson(newContact)
      .then(response => {console.log(response)});
    // updating state
    setPersons(persons.concat(newContact));
    setNewNumber("");
    setNewName("");
  }

  const onFilterChange = (event) => {
    setNewFilter(event.target.value);
  }

  //building final app component
  return (
    <div>
      <h1>Phonebook</h1>
      Search <Filter value={newFilter} onChange={onFilterChange}/>
      <br/>
      <br/>

      <h2>Add a new contact</h2>

      <PersonForm 
        newName={newName}
        onChangeNewName={onChangeNewName}
        newNumber={newNumber}
        onChangeNewNumber={onChangeNewNumber}
        onSubmit={onAddContact}
      />

      <h2>Numbers</h2>
      <Persons setPersons={setPersons} persons={persons} filter={newFilter} onDelete={onDelete}/>
    </div>
  )
}

export default App
