import personService from './services.js'

const Persons = (props) => {
    const {persons, filter, setPersons} = props;
    const filteredPersons = persons.filter(p => p.name.includes(filter))

    return (
        <div>
            {filteredPersons.map(p => {
                const onDelete = () => {
                    if(!window.confirm("Are you sure you want to delete this contact?")){
                        return;
                    }
                    personService.removePerson(p.id).then(() =>{
                        setPersons(persons.filter((per) => per.id !== p.id));
                    })
                    .catch(e => {console.error("Failed to delete")});
